mapper.select:
SELECT title, content, view, point, name, board.created_at, board.updated_at  FROM board JOIN user
ON board.user_id = user.id
WHERE user.is_deleted = 0 AND board.id = #{boardId}

mapper.update:
UPDATE board
SET is_deleted=1
WHERE id = #{boardId}

UPDATE board
SET view = view+1
WHERE id = #{boardId}

UPDATE board
SET point = point + #{point}
WHERE id = #{board_id}
