mapper.update:
UPDATE point
SET is_deleted = 1
WHERE user_id = #{user_id} AND board_id = #{board_id}

UPDATE point
SET is_deleted = 1
WHERE user_id = #{user_id} AND comment_id = #{comment_id}

UPDATE point
SET is_deleted = 0,
    point = #{point}
WHERE user_id = #{user_id} AND board_id = #{board_id}

UPDATE point
SET is_deleted = 0,
    point = #{point}
WHERE user_id = #{user_id} AND comment_id = #{comment_id}
